Property.Value:
BEGIN
SELECT AvgLineCharsPerBook, AvgChapterCharsPerBook, AvgParagraphCharsPerBook, AvgBookCharsPerBook, TotalBooks, AvgWordsPerBook, TotalWords, AvgLinesPerBook, TotalParagraphs, AvgTotalChapters, TotalChapters FROM ( SELECT AVG(AvgCharLine) AS AvgLineCharsPerBook, AVG(AvgCharChapter) AS AvgChapterCharsPerBook, AVG(AvgCharParagraph) AS AvgParagraphCharsPerBook, AVG(AvgCharBook) AS AvgBookCharsPerBook, COUNT([BookStat].[BookID]) AS TotalBooks, AVG(TotalWords) AS AvgWordsPerBook, SUM(TotalWords) AS TotalWords, AVG(TotalLines) AS AvgLinesPerBook, SUM(TotalLines) AS TotalLines, AVG(TotalParagraphs) AS AvgParagraphsPerBook, SUM(TotalParagraphs) AS TotalParagraphs, AVG(TotalChapters) AS AvgTotalChapters, SUM(TotalChapters) AS TotalChapters FROM [dbo].[BookStat] GROUP BY [dbo].[BookStat].[BookID] ) AS a
END;

BEGIN
SELECT [B].[BookContent] AS [BookContent], [B].[BookId] AS [BookId], [B].[BookName] AS [BookName], [B].[BookContent] AS [BookContent] FROM [Book] AS [B] WHERE [B].[BookName] = @BookName
END;

