CREATE PROCEDURE [dbo].[InsertBook](
   @BookName VARCHAR(MAX),
   @Author VARCHAR(MAX),
   @PublishedDate VARCHAR(250),
   @Words [Words] READONLY,
   @BookContent VARCHAR(MAX)
)

AS

BEGIN DECLARE @wordGroupID INT;
INSERT INTO [WordGroup] ( WordGroupName, WordID, BookID ) SELECT @WordGroupName, W.WordID, @BookID FROM Word W INNER JOIN @Words AS WGW ON LEN([WGW].[Word]) = [W].[Length] AND [WGW].[Word] = [W].[Value]
END;

